// features/HomePage.js

import React, { useEffect, useState } from 'react';
import { Typography, Button } from '@mui/material';

const HomePage = () => {
  const [email, setEmail] = useState('');

  useEffect(() => {
    // Check if email exists in local storage on component mount
    const storedEmail = localStorage.getItem('email');
    if (storedEmail) {
      setEmail(storedEmail);
    }
  }, []);

  const handleSignOut = () => {
    // Clear email from local storage
    localStorage.removeItem('email');
    // Clear email state
    setEmail('');
  };

  const handleSignIn = () => {
    window.location.href = "/"; // Navigate to "/"
  };

  return (
    <div>
      <Typography variant="h1" component="h2" gutterBottom>
        Welcome
      </Typography>
      <Typography variant="h3" gutterBottom>
        {email ? `Welcome, ${email}!` : 'Welcome!'}
      </Typography>
      <Typography variant="body1">
        This is your home page. You can add more content here.
      </Typography>
      {email ? (
        <Button variant="contained" color="primary" onClick={handleSignOut}>
          Sign Out
        </Button>
      ) : (
        <Button variant="contained" color="primary" onClick={handleSignIn}>
          Sign In
        </Button>
      )}
    </div>
  );
}

export default HomePage;
