import React, { useState } from 'react';
import { TextField, Button, Typography, Container, Box } from '@mui/material';
import ReCAPTCHA from 'react-google-recaptcha';

function RecruiterLogin() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [captcha, setCaptcha] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleCaptchaChange = (value) => {
        setCaptcha(value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (email && password && captcha) {
            // Your authentication logic here
            // Verify the captcha with your backend server
            // If captcha is verified, proceed with form submission
        } else {
            setErrorMessage('Please fill in all fields.');
        }
    };

    return (
        <Container maxWidth="sm">
            <Box sx={{ marginTop: 4, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Typography variant="h4" component="h2" gutterBottom>Recruiter Login</Typography>
                <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
                    <TextField
                        label="Email"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        fullWidth
                        margin="normal"
                    />
                    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 2 }}>
                        <Typography variant="subtitle1">Captcha:</Typography>
                        <ReCAPTCHA sitekey={process.env.REACT_APP_SITE_KEY} onChange={handleCaptchaChange} />
                    </Box>
                    <Button type="submit" variant="contained" color="primary" sx={{ marginTop: 2 }}>
                        Login
                    </Button>
                    {errorMessage && <Typography style={{ color: 'red', marginTop: 2 }}>{errorMessage}</Typography>}
                </Box>
            </Box>
        </Container>
    );
}

export default RecruiterLogin;
