// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ImageButton from './features/button'; 
import StudentLogin from './features/studentLogin';
import RecruiterLogin from './features/recruiterLogin';

const images = [
  {
    url: process.env.PUBLIC_URL + '/iith.jpg', // Image URL for student
    title: 'Student',
    width: '18%', // Adjusted width for responsiveness
    link: '/student' // Route for student login page
  },
  {
    url: process.env.PUBLIC_URL + '/iith.jpg', // Image URL for recruiter
    title: 'Recruiter',
    width: '18%', // Adjusted width for responsiveness
    link: '/recruiter' // Route for recruiter login page
  },
];

function App() {
  return (
    <Router>
      <div>
        <h2>Choose Your Role</h2>
        <div>
          {images.map((image, index) => (
            <ImageButton key={index} image={image} />
          ))}
        </div>
        <Switch>
          <Route path="/student" component={StudentLogin} />
          <Route path="/recruiter" component={RecruiterLogin} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
