// App.js
import React from 'react';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import reducer from './reducers'; // Assuming you have a reducers.js file where your reducer is defined
import ImageButton from './features/button'; 
import StudentLogin from './features/studentLogin';
import RecruiterLogin from './features/recruiterLogin';

// Create Redux store
const store = createStore(reducer);

function App() {
  return (
    <Provider store={store}>
      <div>
        <h2>Choose Your Role</h2>
        <div>
          {/* Render your image buttons here */}
          {/* For example: */}
          <ImageButton title="Student" width='20%' route="/student" />
          <ImageButton title="Recruiter" width='20%' route="/recruiter" />
        </div>
        {/* Render login components based on the current route */}
        {/* For example: */}
        {window.location.pathname === '/student' && <StudentLogin />}
        {window.location.pathname === '/recruiter' && <RecruiterLogin />}
      </div>
    </Provider>
  );
}

export default App;
