// App.js
import React from 'react';
import { useSelector } from 'react-redux';
import ImageButton from './features/button'; 
import StudentLogin from './features/studentLogin';
import RecruiterLogin from './features/recruiterLogin';

const images = [
  {
    url: process.env.PUBLIC_URL + '/iith.jpg',
    title: 'Student',
    width: '20%',
    route: '/student', // Route for student login page
  },
  {
    url: process.env.PUBLIC_URL + '/iith.jpg',
    title: 'Recruiter',
    width: '20%',
    route: '/recruiter', // Route for recruiter login page
  },
];

function App() {
  const currentRoute = useSelector(state => state.currentRoute);

  return (
    <div>
      <h2>Choose Your Role</h2>
      <div>
        {images.map((image, index) => (
          <ImageButton key={index} image={image} route={image.route} />
        ))}
      </div>
      {/* Conditionally render login components based on the current route */}
      {currentRoute === '/student' && <StudentLogin />}
      {currentRoute === '/recruiter' && <RecruiterLogin />}
    </div>
  );
}

export default App;
