import React, { useState } from 'react';
import { TextField, Button, Typography } from '@mui/material';
import ReCAPTCHA from 'react-google-recaptcha';

function RecruiterLogin() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [captcha, setCaptcha] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleCaptchaChange = (value) => {
        setCaptcha(value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (email && password && captcha) {
            // Your authentication logic here
            // Verify the captcha with your backend server
            // If captcha is verified, proceed with form submission
        } else {
            setErrorMessage('Please fill in all fields.');
        }
    };

    return (
        <div>
            <Typography variant="h4" component="h2" gutterBottom>Recruiter Login</Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    fullWidth
                    margin="normal"
                    style={{ maxWidth: 300, minWidth: 200 }}
                /><></>
                <TextField
                    label="Password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    fullWidth
                    margin="normal"
                    style={{ maxWidth: 300, minWidth: 200 }}
                />
                <div>
                    <Typography variant="subtitle1">Captcha:</Typography>
                    <ReCAPTCHA sitekey={process.env.REACT_APP_SITE_KEY} onChange={handleCaptchaChange} />
                </div>
                <Button type="submit" variant="contained" color="primary">
                    Login
                </Button>
                {errorMessage && <Typography style={{ color: 'red' }}>{errorMessage}</Typography>}
            </form>
        </div>
    );
}

export default RecruiterLogin;
