// reducer.js
const initialState = {
    loggedIn: false,
    userData: null,
    error: null,
  };

  const authReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          loggedIn: true,
          userData: action.payload,
          error: null,
        };
      case 'LOGIN_FAILURE':
        return {
          ...state,
          loggedIn: false,
          userData: null,
          error: action.payload,
        };
      default:
        return state;
    }
  };

  export default authReducer;
