// reducers.js
import { combineReducers } from 'redux';

const initialState = {
  loggedInUser: null,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOGIN':
      return {
        ...state,
        loggedInUser: action.payload,
      };
    case 'LOGOUT':
      return {
        ...state,
        loggedInUser: null,
      };
    default:
      return state;
  }
};

const rootReducer = combineReducers({
  user: userReducer,
});

export default rootReducer;
